#include "defs.h"
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

u32 time_start;

void kiwi_start( void )
{
	int r;
	time_start = time(NULL);
	srand( time_start );
	r=0;

	rand();rand();rand();rand();rand();rand();rand();rand();rand();rand();rand();rand();rand();rand();
	
	switch( r )
	{
	case 0:
		printf( "Your lucky number today is %i!\n", rand() );
		break;
	}

}
